import logo from './logo.svg';
import './App.css';
import {useEffect, useState} from 'react';

function handleError(error) {
	if (error) {
		alert(error.message);
	}
}


const apiKey = '47830111';
const sessionId = '2_MX40NzgzMDExMX5-MTcwMjQ3MjIzNTQxNX5ydzhkVzg0MEZ5aHpQbHlCODVkLzIwSmZ-fn4';
const token = '';

function initSession() {
const OT = window.OT;
	const session = OT.initSession(apiKey, sessionId);
	return session;
}

function initializePub(session) {
	// Create a publisher
const OT = window.OT;
	const publisher = OT.initPublisher('publisher', {
		insertMode: 'replace',
		width: '500px',
		height: '500px',
	}, handleError);

	// Connect to the session
	session.connect(token, error => {
		// If the connection is successful, publish to the session
		if (error) {
			handleError(error);
		} else {
			session.publish(publisher, handleError);
		}
	});
}

function initializeSub() {
	const OT = window.OT;
	const apiKey = '47830111';
	const sessionId = '2_MX40NzgzMDExMX5-MTcwMjQ3MjIzNTQxNX5ydzhkVzg0MEZ5aHpQbHlCODVkLzIwSmZ-fn4';
	const token = '';
	const session = OT.initSession(apiKey, sessionId);

	/* Subscribe to a newly created stream */
	session.on('streamCreated', event => {
		console.log('streamCreated', event);
		session.subscribe(event.stream, 'subscriber', {
			insertMode: 'append',
			width: '500px',
			height: '500px',
		}, handleError);
	});
	session.connect(token, error => {
		// If the connection is successful, publish to the session
		if (error) {
			handleError(error);
		} else {
			/* Session.publish(publisher, handleError); */
		}
	});
}

function Publish() {
	useEffect(() => {
		initializePub();
	});

	return (
		<div>
			<p>Subscribe</p>
			<div style={{backgroundColor: 'red', height: ''}} id='publisher'></div>
		</div>
	);
}

function Subscribe() {
	useEffect(() => {
		initializeSub();
	});

	return (
		<div>
			<p>Publish</p>
			<div style={{backgroundColor: 'red', height: ''}} id='subscriber'></div>
		</div>
	);
}

function App() {
	// Handling all of our errors here by alerting them
	const [session, setSession] = useState();
	useEffect(() => {
		setSession(initSession());
	});
    if (!session)
        return <></>

	return (
		<div className='App'>
			<Publish session={session}/>
			<Subscribe session={session}/>
		</div>
	);
}

export default App;
